import React, { Component } from 'react';
import Detail from './Detail';
class Messages extends Component {

  state = {
    messageArr: [
      { id: 1, title: '消息1' },
      { id: 2, title: '消息2' },
      { id: 3, title: '消息3' },
    ]
  }

  render () {
    const { messageArr } = this.state;
    return (
      <div>
        <ul>
          {messageArr.map(message => {
            return (
              <li key={message.id}>
                <a href="/xxx">{message.title}</a>&nbsp;&nbsp;
              </li>
            )
          })}
        </ul>
        <Detail />
      </div>
    );
  }
}

export default Messages;
