// 函数式组件
function Demo () {
  const [count, setCount] = React.useState(0) // useState 返回一个数组，数组的第一位为状态的值，第二位为改变状态的方法
  // console.log(count, setCount);
  function add() {
    // setCount(count + 1); // 写法一
    setCount(count => count + 1); // 写法二
  }
  return (
    <div>
      <h1>hooks 组件</h1>
      <h2>当前求和为：{count}</h2>
      <button onClick={add}>+1</button>&nbsp;
    </div>
  )
}


export default Demo;
