const DetailData = [
  {id: '1', content: '我爱你中国'},
  {id: '2', content: '我爱你重庆'},
  {id: '3', content: '我爱你妈妈'}
]

class Detail extends Component {
  render() {
    // const {id, title} = this.props.match.params; // 接收 params 参数
    const {search} = this.props.location; // 接收 search 参数
    // console.log(search); // ?id=1$title=消息1
    const {id, title} = qs.parse(search.slice(1));
    console.log(qs.parse(search.slice(1)));
    // const findResult = DetailData.find(data => data.id === id);
    return (
      <ul>
        <li>Id: {id}</li>
        <li>Title: {title}</li>
        {/* <li>Content: {findResult.content}</li> */}
      </ul>
    );
  }
}

export default Detail;
