import React, { Component } from 'react';
import './index.css'
class Parent extends Component {
  state = {carName: '奔驰c36'};
  changeCar = () => {
    this.setState({carName: '迈巴赫'});
  }
  render() {
    return (
      <div className="parent">
        <h2>我是 Parent 组件</h2>
        <button onClick={this.changeCar}>点我换车</button>
        <Child />
      </div>
    );
  }
}
class Child extends Component {
  render() {
    return (
      <div className="child">
        <h2>我是 Child 组件</h2>
      </div>
    );
  }
}

export default Parent;
