import React, { Component } from 'react';

class A extends Component {
  state = {username: 'byl'};
  render() {
    return (
      <div>
        <h3>我是 A 组件</h3>
        <h4>我的用户名是：{this.state.username}</h4>
        <B />
      </div>
    );
  }
}
class B extends Component {
  render() {
    return (
      <div>
        context
        <C />
      </div>
    );
  }
}
class C extends Component {
  render() {
    return (
      <div>
        context
      </div>
    );
  }
}
export default A;
