//         <h4>我从 A 组件接收到的用户名：{this.context}</h4>
//       </div>
//     );
//   }
// }
// 函数式组件接收
function C() {
  return (
    <div className="grand">
      <h3>我是 C 组件</h3>
      <h4>我从 A 组件接收到的用户名：
        {/* <Consumer>
          {
            value => value
          }
        </Consumer> */}
      </h4>
    </div>
  );
}
export default A;
